mapper.select:
SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_ID =#{memberId}

SELECT NICKNAME
FROM MEMBER
WHERE NICKNAME =#{nickname}

SELECT MEMBER_ID, PW, NICKNAME, EMAIL
FROM MEMBER
WHERE MEMBER_ID =#{memberId}

SELECT NICKNAME
FROM MEMBER
WHERE MEMBER_ID != #{memberId} AND NICKNAME =#{nickname}

mapper.update:
UPDATE MEMBER
SET PW = #{pw},
	NICKNAME = #{nickname},
	EMAIL = #{email}
WHERE MEMBER_ID = #{memberId}
